SELECT J.AUTHOR_ID, A.AUTHOR_NAME, J.CATEGORY, J.TOTAL_SALES FROM (SELECT B.AUTHOR_ID, B.CATEGORY, SUM(B.PRICE*S.SALES) "TOTAL_SALES" FROM BOOK B INNER JOIN BOOK_SALES S ON B.BOOK_ID=S.BOOK_ID WHERE TO_CHAR(S.SALES_DATE, 'YYYY-MM')='2022-01' GROUP BY B.AUTHOR_ID, B.CATEGORY) J INNER JOIN AUTHOR A ON A.AUTHOR_ID=J.AUTHOR_ID ORDER BY 1, 3 DESC;